const RESEND_API_URL = "https://api.resend.com/emails";

// Configure your defaults here
const SUPPORT_EMAIL = "robert@airunner2033.com";
const APP_URL = "https://airunner2033.com";

// Logo can be a data URL (inline image) or a normal https URL.
// Replace this value with your own logo URL or data URI if desired.
const LOGO_SRC =
  "data:image/png;base64,";

function createHtml(email: string): string {
  return `
<div style="font-family: system-ui, sans-serif, Arial; font-size: 16px; background-color: #1a1a1a; color: #ffffff; padding: 16px;">
  <div style="max-width: 600px; margin: auto; padding: 16px;">
    <img src="${LOGO_SRC}" alt="AI Runner 2033" width="184" height="40" style="display:block; margin-bottom:16px;" /><br>

    <p>Welcome to the AI Runner 2033 platform! We're excited to have you on board.</p>

    <p>Your account (${email}) has been successfully created, and you're now ready to explore all the great features we offer.</p>

    <a style="display: inline-block; text-decoration: none; outline: none; background-color: #00eaff; color: #000; font-weight: 600; padding: 10px 20px; border-radius: 4px; margin: 12px 0;" href="${APP_URL}">
      Open AI Runner 2033
    </a>

    <p>If you have any questions or need help getting started, you are just an email away at
      <a style="color: #00eaff;" href="mailto:${SUPPORT_EMAIL}">${SUPPORT_EMAIL}</a>.
      We're here to assist you every step of the way!
    </p>

    <p>Best regards,<br>The AI Runner 2033 Team</p>
  </div>
</div>
`.trim();
}

// Cloudflare Pages Function for sending welcome emails via Resend
export const onRequestOptions: PagesFunction = async () => {
  return new Response(null, {
    status: 204,
    headers: {
      "Access-Control-Allow-Origin": "*",
      "Access-Control-Allow-Methods": "POST, OPTIONS",
      "Access-Control-Allow-Headers": "Content-Type, Authorization",
    },
  });
};

export const onRequestPost: PagesFunction = async ({ request, env }) => {
  try {
    // Get Resend API key from environment (Cloudflare Pages) or request header (local dev)
    const apiKey = (env as any)?.RESEND_API_KEY || 
                   request.headers.get("X-Resend-Api-Key") as string | undefined;

      if (!apiKey) {
        return new Response(
          JSON.stringify({ error: "RESEND_API_KEY is not configured" }),
          {
            status: 500,
            headers: {
              "Content-Type": "application/json",
              "Access-Control-Allow-Origin": "*",
            },
          }
        );
      }

      const body = await request.json().catch(() => ({}));
      const email = typeof body.email === "string" ? body.email.trim() : "";

      if (!email) {
        return new Response(JSON.stringify({ error: "Email is required" }), {
          status: 400,
          headers: {
            "Content-Type": "application/json",
            "Access-Control-Allow-Origin": "*",
          },
        });
      }

      const html = createHtml(email);

      const resendResponse = await fetch(RESEND_API_URL, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${apiKey}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          from: `AI Runner 2033 <no-reply@airunner2033.com>`,
          to: [email],
          subject: "Welcome to AI Runner 2033",
          html,
        }),
      });

      if (!resendResponse.ok) {
        const errorText = await resendResponse.text().catch(() => "");
        console.error("Resend error:", resendResponse.status, errorText);

        return new Response(
          JSON.stringify({
            error: "Failed to send welcome email",
            status: resendResponse.status,
          }),
          {
            status: 500,
            headers: {
              "Content-Type": "application/json",
              "Access-Control-Allow-Origin": "*",
            },
          }
        );
      }

      return new Response(JSON.stringify({ success: true }), {
        status: 200,
        headers: {
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": "*",
        },
      });
  } catch (error: any) {
    console.error("sendWelcomeEmail error:", error);
    return new Response(
      JSON.stringify({
        error: error?.message || "Internal server error",
      }),
      {
        status: 500,
        headers: {
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": "*",
        },
      }
    );
  }
};


